mapper.select:
   	 	SELECT s.sample_no, s.sample_id,  s.sample_pw, sf.samplefile_path, sf.samplefile_name, sf.samplefile_ext, sf.samplefile_type, sf.samplefile_size, sf.samplefile_date
	 	From sample s INNER JOIN samplefile sf on s.sample_no = sf.sample_no WHERE		   
   	
   			   s.${searchType} LIKE CONCAT('%',#{searchQuery},'%')
   	
  	
   			   s.${searchType} = #{searchQuery} 
   	
   	ORDER BY s.sample_no DESC limit #{currentPage}, #{rowPerPage}
